from collections import Counter


# https://leetcode.com/problems/bulls-and-cows/
class Solution:
    def getHint(self, secret: str, guess: str) -> str:
        count_1, count_2 = Counter(secret), Counter(guess)
        b = sum([min(count_1[k], count_2[k]) for k in count_1])
        a = len([1 for i in range(len(secret)) if secret[i] == guess[i]])
        b -= a
        return f"{a}A{b}B"


if __name__ == "__main__":
    s = Solution()
    print(s.getHint(secret = "1807", guess = "7810"))  # 1A3B
    print(s.getHint(secret = "1123", guess = "0111"))  # 1A1B
    print(s.getHint("",
                    ""))
